namespace = my_events

my_events.2 = {
	type = state_event
	placement = scope:target_state

	title = my_events.2.t
	desc = my_events.2.d

	duration = 3

	trigger = {
		exists = scope:target_state
	}

	immediate = {

	}

	option = {
		name = my_events.2.a
		default_option = yes
	}
}

namespace = my_events

my_events.3 = {
	type = character_event
	placement = root

	title = my_events.3.t
	desc = my_events.3.d

	duration = 3

	trigger = {
		exists = scope:target_character
		scope:target_character = {
			is_busy = no
		}
	}

	immediate = {

	}

	option = {
		name = my_events.3.a
		default_option = yes
	}
}

my_decision = {
	is_shown = {
		# When to show this decision
		NOT = { has_variable = my_decision_done }
	}

	possible = {
		# Requirements to take decision
		is_at_war = no
		has_technology_researched = nationalism
	}

	when_taken = {
		# Effects when decision is taken
		set_variable = my_decision_done
		add_modifier = {
			name = my_decision_modifier
			months = 120
		}
		trigger_event = { id = my_events.1 }
	}

	ai_chance = {
		base = 100
		modifier = {
			trigger = {
				# AI conditions
			}
			add = 50
		}
	}
}

je_my_journal = {
	icon = "gfx/interface/icons/event_icons/event_default.dds"
	group = je_group_historical_content

	is_shown_when_inactive = no

	possible = {
		# When journal entry can activate
		has_technology_researched = nationalism
	}

	immediate = {
		# Effects when journal activates
	}

	complete = {
		# Completion conditions
		scope:journal_entry = {
			is_goal_complete = yes
		}
	}

	on_complete = {
		# Rewards
		trigger_event = { id = my_events.1 }
		add_modifier = {
			name = je_completion_reward
			months = 120
		}
	}

	fail = {
		# Failure conditions
	}

	on_fail = {
		# Failure effects
	}

	progressbar = yes

	current_value = {
		value = root.gdp
	}

	goal_add_value = {
		value = 1000000
	}

	weight = 1000

	should_be_pinned_by_default = yes
}

my_custom_trigger = {
	OR = {
		has_law = law_type:law_monarchy
		has_law = law_type:law_theocracy
		has_law = law_type:law_presidential_republic
	}
	NOT = {
		is_subject = yes
	}
	any_scope_state = {
		is_incorporated = yes
		state_region = s:STATE_REGION
	}
}

my_custom_effect = {
	add_modifier = {
		name = my_modifier
		months = 24
	}

	add_loyalists = {
		value = 0.1
		strata = middle
	}

	every_scope_state = {
		limit = {
			is_incorporated = yes
		}
		add_modifier = {
			name = state_modifier
			months = 12
		}
	}

	if = {
		limit = {
			has_law = law_type:law_monarchy
		}
		# Monarchy specific effects
	}
}

building_my_building = {
	building_group = bg_manufacturing

	texture = "gfx/interface/icons/building_icons/building_my_building.dds"

	city_type = city

	levels_per_mesh = 5

	unlocking_technologies = {
		manufacturies
	}

	production_method_groups = {
		pmg_my_building_process
		pmg_my_building_automation
		pmg_ownership_capital_building_manufacturing
	}

	required_construction = construction_cost_medium

	ai_value = 1000

	should_auto_expand = {
		default_auto_expand_rule = yes
	}
}

law_my_custom_law = {
	group = lawgroup_governance_principles

	icon = "gfx/interface/icons/law_icons/my_law.dds"

	progressiveness = 50

	unlocking_technologies = {
		democracy
	}

	disallowing_laws = {
		law_autocracy
	}

	modifier = {
		country_legitimacy_govt_total_votes_add = 20
		country_authority_mult = -0.1
	}

	possible_political_movements = {
		law_universal_suffrage
	}

	ai_enact_weight_modifier = {
		value = 0
	}
}

my_technology = {
	era = era_3

	texture = "gfx/interface/icons/technology_icons/my_technology.dds"

	category = production

	unlocking_technologies = {
		manufacturies
		mechanical_tools
	}

	modifier = {
		country_weekly_innovation_mult = 0.05
		building_group_bg_manufacturing_throughput_mult = 0.1
	}

	ai_weight = {
		value = 1
	}
}

c:COUNTRY_TAG = {
	create_character = {
		first_name = "John"
		last_name = "Smith"
		birth_date = 1820.5.15

		traits = {
			experienced_political_operator
			charismatic
		}

		ruler = yes

		culture = cu:british
		religion = rel:protestant
		ideology = ideology_reformer
	}
}

ig_my_interest_group = {
	color = hsv{ 0.55 0.8 0.5 }

	texture = "gfx/interface/icons/ig_icons/my_ig.dds"

	layer = "revolution_dynamic_my_ig"

	index = 10

	ideologies = {
		ideology_reformer
		ideology_liberal
	}

	enable = {
		always = yes
	}

	pop_potential = {
		NOT = { is_pop_type = peasants }
		literacy_rate > 0.25
	}

	pop_weight = {
		value = 0
	}
}

my_diplomatic_action = {
	groups = { general }

	requires_approval = yes
	should_notify_third_parties = yes

	texture = "gfx/interface/icons/diplomatic_action_icons/my_action.dds"

	selectable = {
		NOT = { is_country_type = decentralized }
	}

	potential = {
		scope:target_country = {
			NOT = { is_country_type = decentralized }
		}
	}

	possible = {
		has_diplomatic_relevance = scope:target_country
		NOT = { has_war_with = scope:target_country }
	}

	accept_effect = {
		# Effects when accepted
	}
}

company_type_my_company = {
	texture = "gfx/interface/icons/company_icons/my_company.dds"

	flavored_company = no

	building_types = {
		building_textile_mills
		building_furniture_manufacturies
	}

	attainable = {
		has_technology_researched = manufacturies
	}

	potential = {
		always = yes
	}

	possible = {
		any_scope_building = {
			is_building_type = building_textile_mills
			occupancy >= 0.8
		}
	}

	prosperity_modifier = {
		building_group_bg_manufacturing_throughput_mult = 0.1
	}
}

on_monthly_pulse_country = {
	random_events = {
		100 = 0
		10 = my_events.1
		5 = my_events.2
	}

	effect = {
		if = {
			limit = {
				has_variable = my_counter
			}
			change_variable = {
				name = my_counter
				add = 1
			}
		}
	}
}

my_scripted_gui = {
	scope = country

	is_shown = {
		has_technology_researched = nationalism
	}

	ai_is_valid = {
		always = no
	}

	effect = {
		custom_tooltip = {
			text = my_gui_tooltip

			add_modifier = {
				name = my_modifier
				months = 12
			}
		}
	}

	cost = {
		authority = 50
	}
}

