# Every - iterates all matching
every_scope_state = {
	limit = {
		is_incorporated = yes
	}
	add_modifier = {
		name = my_modifier
		months = 12
	}
}

# Random - picks one random
random_scope_state = {
	limit = {
		is_incorporated = yes
	}
	save_scope_as = target_state
}

my_script_value = {
	value = 0

	add = 100

	if = {
		limit = {
			has_law = law_type:law_monarchy
		}
		add = 50
	}

	multiply = {
		value = gdp
		divide = 1000000
	}

	min = 10
	max = 1000
}
